import React, { useState } from "react";


export default function AddMenu() {

  const [names, setnames] = useState([{name:" ", pass:" "}]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:5000/add/Food", {
      method: "POST",
      headers: {
        "Content-Type": "application.json",
      },
      body: JSON.stringify({name:names.name, pass: names.pass}),
    });
    const json = await response.json()
    console.log(json)
  };

  const changeon = (e) => {
    setnames({ ...names, [e.target.name]: e.target.value });
  };
  
  return (
    <div>
      <div className="container mt-5">
        <h2>Dish Information</h2>
        <form onSubmit={handleSubmit}>
          {/* <!-- Dish Name Input --> */}
          <div className="form-group">
            <label htmlFor="dishName">Dish Name:</label>
            <input
              onChange={changeon}
              name="name"
              value={names.name}
              type="text"
              className="form-control"
              id="dishName"
              placeholder="Enter the name of the dish"
            />
          </div>

          {/* <!-- Image URL Input --> */}
          <div className="form-group">
            <label htmlFor="imageUrl">Image URL:</label>
            <input
              onChange={changeon}
              name="pass"
              value={names.pass}
              type="text"
              className="form-control"
              id="imageUrl"
              placeholder="Enter the image URL"
            />
          </div>

          {/* <!-- Description Input --> */}
          {/* <div className="form-group">
                <label htmlFor="description">Description:</label>
                <textarea className="form-control" id="description" rows="4" placeholder="Enter a description"></textarea>
            </div> */}

          {/* <!-- Price Input --> */}
          {/* <div className="form-group">
                <label htmlFor="price">Price:</label>
                <input type="number" className="form-control" id="price" placeholder="Enter the price"/>
            </div> */}

          {/* <!-- Submit Button --> */}
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
