import React from "react";
import { useState } from "react";

export default function addMenu() {
  const [name, setname] = useState({ name: "", pass: "" });
  const handleSubmit = async () => {
    const response = fetch("http://localhost/add/Food", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(),
    });
    
  }
  const changeon = (e) => {
    setname({ ...name, [e.target.name]: e.target.value });
  };
  ;
  return (
    <div>
      <div className="container mt-5">
        <h2>Dish Information</h2>
        <form onSubmit={handleSubmit}>
          {/* <!-- Dish Name Input --> */}
          <div className="form-group">
            <label htmlFor="dishName">Dish Name:</label>
            <input 
              onChange={changeon}
              name="name"
              value={name.name}
              type="text"
              className="form-control"
              id="dishName"
              placeholder="Enter the name of the dish"
              
            />
          </div>

          {/* <!-- Image URL Input --> */}
          <div className="form-group">
            <label htmlFor="imageUrl">Image URL:</label>
            <input
              onChange={changeon}
              name="pass"
              value={name.pass}
              type="text"
              className="form-control"
              id="imageUrl"
              placeholder="Enter the image URL"
             
            />
          </div>

          {/* <!-- Description Input --> */}
          {/* <div className="form-group">
                <label htmlFor="description">Description:</label>
                <textarea className="form-control" id="description" rows="4" placeholder="Enter a description"></textarea>
            </div> */}

          {/* <!-- Price Input --> */}
          {/* <div className="form-group">
                <label htmlFor="price">Price:</label>
                <input type="number" className="form-control" id="price" placeholder="Enter the price"/>
            </div> */}

          {/* <!-- Submit Button --> */}
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
